
public class peseta_euros {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		double euros = 4;
		double pesetas = 45600; 
		double factor_conversion = 166.38;
	
		System.out.println(euros+" Euros son "+euros*factor_conversion+" pesetas");
		System.out.println(pesetas+" Pesetas son "+pesetas/factor_conversion+" Euros");
		
	}

}
